mapper.select:
SELECT 
${item}
FROM
projectCommit
WHERE
id=#{projectId} AND `day` >= #{startDay} AND `day` <= #{endDay} order by `day` asc;

SELECT 
${item}
FROM
projectCommit
WHERE
id=#{projectId} AND `day` >= #{startDay} AND `day` <= #{endDay} order by `day` asc;

mapper.insert:
INSERT INTO projectEvent(id, team, `day`, push, issue, comment, `create`,total) 
VALUES
(#{id},#{team},#{day},#{push},#{issue},#{comment},#{create},#{total});

mapper.update:
UPDATE projectEvent
SET
team=#{team}

	,push=#{push}


	,issue=#{issue}


	,comment=#{comment}


	,`create`=#{create}


	,total=#{total}

WHERE
team=#{team} AND `day`=#{day};

UPDATE projectCommit 
SET
team=#{team}

	,commit_count=#{commit_count}


	,add_line=#{add_line}


	,delete_line=#{delete_line}


	,java_file=#{java_file}


	,total_commit=#{total_commit}


	,total_add=#{total_add}


	,total_delete=#{total_delete}

WHERE
team=#{team} AND `day`=#{day};
