mapper.select:
SELECT 
${item}
FROM
studentCommit
WHERE
student=#{student} AND `day` >= #{startDay} AND `day` <= #{endDay} order by `day` asc;

mapper.insert:
INSERT INTO studentEvent(id, student, day, push, issue, comment, create,total) 
VALUES
(#{id},#{student},#{day},#{push},#{issue},#{comment},#{create},#{total});

mapper.update:
UPDATE studentEvent
SET
student=#{student}

	,push=#{push}


	,issue=#{issue}


	,comment=#{comment}


	,create=#{create}


	,total=#{total}

WHERE
student=#{student} AND day=#{day};

UPDATE studentCommit 
SET
student=#{student}

	,commit_count=#{commit_count}


	,add_line=#{add_line}


	,delete_line=#{delete_line}


	,java_file=#{java_file}


	,total_commit=#{total_commit}


	,total_add=#{total_add}


	,total_delete=#{total_delete}

WHERE
student=#{student} AND day=#{day};
